package test.base;

import org.junit.Test;
import org.junit.experimental.categories.Category;
import pages.SearchResultPage;
import pages.StartPage;
import test.category.BaseCategory;

import static org.junit.Assert.assertEquals;

@Category(BaseCategory.class)
public class TruncateSearchTermTest {

    private SearchResultPage searchResultPage;

    public TruncateSearchTermTest() {
        searchResultPage = new SearchResultPage().openPage(SearchResultPage.class);
    }

    @Test
    public void truncatesStringTo100() {
        String string1024Char = "";
        String string100Char = "Sökresultat för " + string1024Char.substring(0, 100);
        searchResultPage.searchFor(string1024Char);

        assertEquals(string100Char, searchResultPage.getSearchResultLabel());
    }
}
